CREATE TABLE `orders` (
                          `order_id` varchar(20) NOT NULL COMMENT '订单ID',
                          `user_id` varchar(20) NOT NULL COMMENT '用户ID',
                          `order_time` datetime NOT NULL COMMENT '下单时间',
                          `total_amount` decimal(10,2) NOT NULL COMMENT '订单总金额',
                          `order_status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '订单状态:0-待付款,1-已付款,2-已发货,3-已完成,4-已取消',
                          `payment_method` varchar(20) DEFAULT NULL COMMENT '支付方式',
                          `shipping_address` varchar(255) DEFAULT NULL COMMENT '收货地址',
                          `contact_phone` varchar(20) DEFAULT NULL COMMENT '联系电话',
                          PRIMARY KEY (`order_id`),
                          KEY `idx_user_id` (`user_id`),
                          KEY `idx_order_time` (`order_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='订单表';

CREATE TABLE `order_details` (
                                 `detail_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '详情ID',
                                 `order_id` varchar(20) NOT NULL COMMENT '订单ID',
                                 `product_id` varchar(20) NOT NULL COMMENT '商品ID',
                                 `product_name` varchar(100) NOT NULL COMMENT '商品名称',
                                 `quantity` int(11) NOT NULL COMMENT '购买数量',
                                 `unit_price` decimal(10,2) NOT NULL COMMENT '单价',
                                 `total_price` decimal(10,2) NOT NULL COMMENT '总价',
                                 `specification` varchar(100) DEFAULT NULL COMMENT '商品规格',
                                 PRIMARY KEY (`detail_id`),
                                 KEY `idx_order_id` (`order_id`),
                                 KEY `idx_product_id` (`product_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='订单详情表';

